mapper.insert:
 		insert into ta_recruit_activation
		(
		activationRegisterNo
		,applicantNo
		, activationName
		, activationKind
		, activationContent
		, activationRole
		, activationStartDate
		, activationEndDate
		, activationOrgan
		)
		values
		(
		(SELECT IFNULL(MAX(activationRegisterNo),0) FROM ta_recruit_activation A)+1
		, #{applicantNo}
		, #{activationName}
		, #{activationKind}
		, #{activationContent}
		, #{activationRole}
		, #{activationStartDate}
		, #{activationEndDate}
		, #{activationOrgan}
		);
