mapper.insert:
 		insert into ta_recruit_career
		(
		careerRegisterNo
		,applicantNo
		, careerName
		, careerContract
		, careerPart
		, careerRole
		, careerStartDate
		, careerEndDate
		, careerContent
		)
		values
		(
		(SELECT IFNULL(MAX(careerRegisterNo),1) FROM ta_recruit_career C)+1
		, #{applicantNo}
		, #{careerName}
		, #{careerContract}
		, #{careerPart}
		, #{careerRole}
		, #{careerStartDate}
		, #{careerEndDate}
		, #{careerContent}
		);
