mapper.insert:


insert
into ta_recruit_college
(
collegeRegisterNo
, applicantNo
, collegeName
, collegeLocation
, collegeEnter
, collegeDegree
, collegeMajor1
, collegeDoubleMajorKind
, collegeMajor2
, collegeScore
, collegeScoreMax
, collegeStartDate
, collegeEndDate
, collegeGraduate

) values
(
(SELECT IFNULL(MAX(collegeRegisterNo),0) FROM ta_recruit_college C)+1
, #{applicantNo}
, #{collegeName}
, #{collegeLocation}
, #{collegeEnter}
, #{collegeDegree}
, #{collegeMajor1}
, #{collegeDoubleMajorKind}
, #{collegeMajor2}
, #{collegeScore}
, #{collegeScoreMax}
, #{collegeStartDate}
, #{collegeEndDate}
, #{collegeGraduate}

);
