mapper.insert:
INSERT INTO
TA_RECRUIT_MEMBER(
MEMBERNAME
, MEMBERBIRTH
, MEMBERID
, MEMBERPW
, MEMBERROOT
, MEMBERPHONENUMBER1
, MEMBERPHONENUMBER2
, MEMBERPHONENUMBER3
, MEMBERISADMIN
, MEMBERREGDATE
)
VALUES(
#{memberName}
, #{memberBirth}
, #{memberId}
, #{memberPw}
, #{memberRoot}
, #{memberPhoneNumber1}
, #{memberPhoneNumber2}
, #{memberPhoneNumber3}
, 0
, SYSDATE()

)

mapper.select:
SELECT
*
FROM
TA_RECRUIT_MEMBER
WHERE
MEMBERID = #{memberId}
AND
MEMBERNAME = #{memberName}
AND
MEMBERBIRTH = #{memberBirth}
AND
MEMBERPHONENUMBER1 = #{memberPhoneNumber1}
AND
MEMBERPHONENUMBER2 = #{memberPhoneNumber2}
AND
MEMBERPHONENUMBER3 = #{memberPhoneNumber3}

mapper.update:
update ta_recruit_member
set
MEMBERPW = #{memberPw}
where
MEMBERID = #{memberId}
